import React, { useCallback, useEffect, useState } from "react";
import ApiService from "../../ApiService";
import { TextField } from "@material-ui/core";
import { Button, Typography } from "@mui/material";

const EditUser = () => {
  const [users, setUser] = useState([]);

  const fetchUserByIDHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await ApiService.fetchUsers();
      if (response.status < 200 || response.status > 299) {
        throw new Error("Something went wrong!");
      }
      const data = await response.data;
      console.log(data);
      setUsers(data);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchUserByIDHandler();
  }, [fetchUserByIDHandler]);

  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  saveUser = (e) => {
    e.preventDefault();

    let user = {
      id: this.state.id,
      password: this.state.password,
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      age: this.state.age,
      salary: this.state.salary,
    };

    ApiService.editUser(user)
      .then((res) => {
        this.setState({
          message: user.lastName + "님 정보가 수정되었습니다.",
        });
        this.props.history.push("/users");
      })
      .catch((err) => {
        console.log("saveUser() 에러", err);
      });
  };

  return (
    <div>
      <Typography variant="h4" style={style}>
        Edit User
      </Typography>
      <form>
        <TextField
          type="text"
          name="username"
          readOnly={true}
          fullWidth
          margin="normal"
          value={this.state.username}
        />

        <TextField
          placeholder="Edit your first name"
          name="firstName"
          fullWidth
          margin="normal"
          value={this.state.firstName}
          onChange={this.onChange}
        />

        <TextField
          placeholder="Edit your last name"
          name="lastName"
          fullWidth
          margin="normal"
          value={this.state.lastName}
          onChange={this.onChange}
        />

        <TextField
          type="number"
          placeholder="Edit your age"
          name="age"
          fullWidth
          margin="normal"
          value={this.state.age}
          onChange={this.onChange}
        />

        <TextField
          type="number"
          placeholder="Edit your salary"
          name="salary"
          fullWidth
          margin="normal"
          value={this.state.salary}
          onChange={this.onChange}
        />

        <Button variant="contained" color="primary" onClick={this.saveUser}>
          Save
        </Button>
      </form>
    </div>
  );
};

const style = {
  display: "flex",
  justifyContent: "center",
};

export default EditUser;
