import React, { useState, useEffect, useCallback } from "react";
import { useNavigate, useLocation } from "react-router";
import ApiService from "../../ApiService";
import { IconButton, InputAdornment, TextField, Typography } from "@mui/material";
import { Button } from "@material-ui/core";
import { Box } from "@mui/system";

const EditUser = (props) => {
  const navigate = useNavigate();
  const location = useLocation();

  const [user, setUser] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUserByIDHandler = useCallback(async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await ApiService.fetchUserByID(location.state.id);
      if (response.status < 200 || response.status > 299) {
        throw new Error("Something went wrong!");
      }
      const data = await response.data;
      setUser(data);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    fetchUserByIDHandler();
  }, [fetchUserByIDHandler]);

  const editUserHandler = (e) => {
    e.preventDefault();
    var formData = new FormData();
    for (var key in user) {
      if (books[key] !== undefined) {
        formData.append(key, user[key]);
      }
    }

    ApiService.editUser(formData)
      .then((res) => {
        console.log(formData.get("name") + "의 정보가 수정되었습니다.");
        navigate("/users");
      })
      .catch((err) => {
        console.log("editBookHandler() Error!", err);
      });
  };

  const onChangeHandler = (e) => {
    setUser({
      ...user,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div>
      <Typography variant="h6" style={style}>
        <Box sx={{ textTransform: "uppercase" }}>{user.name}</Box>의 회원 정보
        수정
      </Typography>
      <form>
        <TextField
          name="id"
          type="text"
          label="구분 아이디"
          readOnly={true}
          fullWidth
          margin="normal"
          value={user.id || ""}
          onChange={onChangeHandler}
        />

        <TextField
          name="name"
          type="text"
          label="회원명"
          fullWidth
          margin="normal"
          value={user.name || ""}
          onChange={onChangeHandler}
        />

        <TextField
          name="pw"
          type={showPassword ? "text" : "password"}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={handleClickShowPassword}
                onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                {showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          }
          label="비밀번호"
          fullWidth
          margin="normal"
          value={user.pw || ""}
          onChange={onChangeHandler}
        />

        <Button variant="contained" color="primary" onClick={editUserHandler}>
          Save
        </Button>
      </form>
    </div>
  );
};

const style = {
  display: "flex",
  justifyContent: "center",
};

export default EditUser;
